	<script type="text/javascript">
		
		$(document).ready(function(){

			var is_valid_username = false;
			var is_valid_password = false;

			function check_username(){
				if($("#username_email").val().length > 0){
					if($("#password").val().length > 6){
						$("#login_btn").removeAttr("disabled");
						is_valid_username = true;
						is_valid_password = true;
					}
					else{
						$("#login_btn").attr("disabled","disabled");
						is_valid_username = true;
						is_valid_password = false;
					}
				}
				else{
					$("#login_btn").attr("disabled","disabled");
					is_valid_username = false;
				}
			}

			function check_password(){
				if($("#password").val().length > 6){
					if($("#username_email").val().length > 0){
						$("#login_btn").removeAttr("disabled");
						is_valid_username = true;
						is_valid_password = true;
					}
					else{
						$("#login_btn").attr("disabled","disabled");
						is_valid_username = false;
						is_valid_password = true;
					}
				}
				else{
					$("#login_btn").attr("disabled","disabled");
					is_valid_password = false;
				}
			}

			$("#username_email").on("keydown keyup",function(){
				check_username();
			});

			$("#password").on("keydown keyup",function(){
				check_password();
			});

			$("#login_form").on("submit",function(){

				is_valid_username = false;
				is_valid_password = false;

				check_username();
				check_password();

				console.log("is_valid_username : "+is_valid_username);
				console.log("is_valid_password : "+is_valid_password);

				if(is_valid_username === true && is_valid_password === true){	
					return true;
				}
				else{
					return false;
				}

			});

		});

	</script>
	<?php include_once('includes/footer.inc.php'); ?>
</body>
</html>